import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import styled from 'styled-components';

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { registerUser, error, loading } = useGlobalContext();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newUser = { username, email, password };
    await registerUser(newUser, navigate);
  };

  return (
    <RegisterStyled>
      <InnerLayout>
        <div className="register-content">
          <div className="card">
            <h2 className="card-title text-center text-pink">Register for Expense Manager</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="username" className="form-label">Username</label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password" className="form-label">Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit"  disabled={loading}>
                {loading ? 'Registering...' : 'Register'}
              </button>
            </form>
            {error && <p className="error-message text-danger text-center">{error}</p>}
            <p className="text-center">
              Already have an account? <Link to="/login" className="text-pink">Log in here</Link>
            </p>
          </div>
        </div>
      </InnerLayout>
    </RegisterStyled>
  );
}

const RegisterStyled = styled.div`
  display: flex;
  .register-content {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 10vh;
    .card {
      background: #FCF6F9;
      border: 2px solid #FFFFFF;
      box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
      border-radius: 20px;
      padding: 2rem;
      width: 100%;
      max-width: 400px;
      .card-title {
        font-size: 1.8rem;
        margin-bottom: 1.5rem;
      }
      .form-group {
        margin-bottom: 1.5rem;
        .form-control {
          border-radius: 5px;
        }
      }
      .btn {
        background-color: var(--color-pink);
        color: white;
        font-weight: bold;
      }
      .error-message {
        margin-top: 1rem;
        font-size: 0.9rem;
        color: var(--color-red);
      }
    }
  }
`;

export default Register;
