import React, { useState, useMemo } from 'react';
import styled from "styled-components";
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import { useGlobalContext } from './context/globalContext';
import Login from './Components/Login/Login';
import Register from './Components/Register/Register';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';

function App() {
  const [active, setActive] = useState(1);
  const global = useGlobalContext();
  console.log(global);

  // Memoize the Orb component
  const orbMemo = useMemo(() => {
    return <Orb />;
  }, []);

  return (
    <Router>
      <AppContent active={active} setActive={setActive} orbMemo={orbMemo} />
    </Router>
  );
}

function AppContent({ active, setActive, orbMemo }) {
  const location = useLocation();

  // Check if the current route is login or register
  const isAuthRoute = location.pathname === '/login' || location.pathname === '/register';

  return (
    <AppStyled bg={bg} className="App">
      {/* Render only the login or register component on the auth routes */}
      {isAuthRoute ? (
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      ) : (
        // Render the main layout and routes for other components
        <>
          {orbMemo}
          <MainLayout>
            <Navigation active={active} setActive={setActive} />
            <main>
              <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/income" element={<Income />} />
                <Route path="/expenses" element={<Expenses />} />
                <Route path="/dashboard" element={<Dashboard />} />
              </Routes>
            </main>
          </MainLayout>
        </>
      )}
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
  body {
    margin: 0;
    padding: 0;
    font-family: Arial, sans-serif;
  }
  .bg-gradient {
    background: linear-gradient(135deg, #FFC0CB, #FFB6C1, #FF69B4);
  }
  .text-pink {
    color: #FF1493;
  }
  .btn-pink {
    background-color: #FF1493;
    border-color: #FF1493;
    color: white;
  }
  .btn-pink:hover {
    background-color: #FF69B4;
    border-color: #FF69B4;
  }
  .card {
    border-radius: 15px;
    border: none;
  }
  .form-control:focus {
    border-color: #FF69B4;
    box-shadow: 0 0 0 0.2rem rgba(255, 105, 180, 0.25);
  }
  a {
    color: #FF1493;
    text-decoration: none;
  }
  a:hover {
    color: #FF69B4;
    text-decoration: underline;
  }
`;

export default App;
