import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useGlobalContext } from '../context/globalContext';  // Import the custom hook

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { registerUser } = useGlobalContext();  // Destructure registerUser from context

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Registration attempt:', { username, email, password });
    
    const success = await registerUser(username, email, password);
    if (success) {
      alert('Registration successful');
      // Redirect to login or dashboard page, if needed
    } else {
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center bg-gradient">
      <div className="card shadow-lg" style={{ width: '100%', maxWidth: '400px' }}>
        <div className="card-body">
          <h2 className="card-title text-center mb-4 text-pink">Register for Expense Manager</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Username</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email</label>
              <input
                type="email"
                className="form-control"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-pink w-100 mb-3">Register</button>
          </form>
          <p className="text-center">
            Already have an account? <Link to="/login" className="text-pink">Log in here</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Register;
